mapper.update:
update cart_ck
set cart_volume = #{cartVolume}
where cart_no = #{cartNo}

mapper.select:
select cart_no, cart_volume, user_id, product_code
from cart_ck
where cart_no = #{cartNo}

select count(*)
from cart_ck
where user_id = #{userId}
and product_code = #{productCode}

